let score,tries,difficult,problem;function getRndInteger(e,t){return Math.floor(Math.random()*(t-e+1))+e}function clearResults(){document.getElementById("inpResult").value="",document.getElementById("inpResult").focus()}function loadMultiplicationProblem(){problem="multiplication",firstNumber=getRndInteger(0,parseInt(25*difficult)),secondNumber=getRndInteger(0,parseInt(10*difficult)),tries+=1,document.getElementById("lblProblem").innerHTML="Quanto é "+firstNumber+" x "+secondNumber+"?",clearResults()}function solveMultiplicationProblem(){return updateScore(document.getElementById("inpResult").value,firstNumber*secondNumber),loadMultiplicationProblem(),!1}function loadFractionProblem(){problem="fraction",numerator=getRndInteger(1,parseInt(10*difficult)),denominator=getRndInteger(2,parseInt(10*difficult)),number=denominator*getRndInteger(1,parseInt(10*difficult)),tries+=1,document.getElementById("lblProblem").innerHTML="Quanto é "+numerator+"/"+denominator+" de "+number+"?",clearResults()}function solveFractionProblem(){return updateScore(document.getElementById("inpResult").value,number/denominator*numerator),loadFractionProblem(),!1}function updateScore(e,t){score+=e==t?1:0,document.getElementById("lblScore").innerText="Pontuação: "+score+" de "+tries+" tentativas.",updateDifficult()}function updateDifficult(){difficult=score/tries*(parseInt(tries/10)+1),document.getElementById("lblDifficult").innerHTML="Dificuldade: "+difficult.toFixed(2)}score=0,tries=0,difficult=1,document.getElementById("inpResult").addEventListener("keyup",function(e){if(13===e.keyCode)switch(problem){case"multiplication":solveMultiplicationProblem();break;case"fraction":solveFractionProblem()}});