let score,tries,problem;function getRndInteger(e,t){return Math.floor(Math.random()*(t-e+1))+e}function loadMultiplicationProblem(){problem="multiplication",firstNumber=getRndInteger(0,25),secondNumber=getRndInteger(0,10),tries+=1,document.getElementById("lblProblem").innerHTML="Quanto é "+firstNumber+" x "+secondNumber+"?",document.getElementById("inpResult").value="",document.getElementById("inpResult").focus()}function solveMultiplicationProblem(){let e=document.getElementById("inpResult").value;const t=firstNumber*secondNumber;return score+=e==t?1:0,document.getElementById("lblScore").innerText="Pontuação: "+score+" de "+tries+" tentativas.",loadMultiplicationProblem(),!1}function loadFractionProblem(){problem="fraction",numerator=getRndInteger(1,10),denominator=getRndInteger(2,10),number=denominator*getRndInteger(1,10),tries+=1,document.getElementById("lblProblem").innerHTML="Quanto é "+numerator+"/"+denominator+" de "+number+"?",document.getElementById("inpResult").value="",document.getElementById("inpResult").focus()}function solveFractionProblem(){let e=document.getElementById("inpResult").value;const t=number/denominator*numerator;return score+=e==t?1:0,document.getElementById("lblScore").innerText="Pontuação: "+score+" de "+tries+" tentativas.",loadFractionProblem(),!1}score=0,tries=0,document.getElementById("inpResult").addEventListener("keyup",function(e){if(13===e.keyCode)switch(problem){case"multiplication":solveMultiplicationProblem();break;case"fraction":solveFractionProblem()}});